(206, 248, 'Наши Сестры милосердия', 'sestrynstvo', '', '<p>Сестры милосердия храма Почаевской иконы Божьей Матери при больнице ОХМАТДЕТ с детками в отделениях больницы.</p>\r\n<p><img src="images/sestrynstvo/img_20160319_212731.jpg" alt="" />', '<img src="images/sestrynstvo/dsc_0210.jpg" alt="" /><img src="images/sestrynstvo/dsc_0207.jpg" alt="" /></p>', 1, 0, 0, 13, '2016-03-24 06:19:29', 43, '', '2016-03-24 06:26:53', 43, 43, '2016-03-24 06:26:53', '2016-03-24 06:19:29', '0000-00-00 00:00:00', '', '', '{"show_title":"","link_titles":"","show_intro":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","alternative_readmore":"","article_layout":""}', 2, 0, 9, '', '', 1, 166, '{"robots":"","author":"","rights":"","xreference":""}', 1, 'ru-RU', ''),
(207, 249, 'Наші Сестри милосердя ', 'sestrynstvo', '', '<p>Сестри милосердя храму Почаївської ікони Божої матері при лікарні ОХМАТДИТ з дітками у відділеннях.</p>\r\n<p><img src="images/sestrynstvo/img_20160319_212731.jpg" alt="" />', '<img src="images/sestrynstvo/dsc_0210.jpg" alt="" /><img src="images/sestrynstvo/dsc_0207.jpg" alt="" /></p>', 1, 0, 0, 12, '2016-03-24 06:22:01', 43, '', '2016-03-24 06:28:39', 43, 43, '2016-03-25 15:21:32', '2016-03-24 06:22:01', '0000-00-00 00:00:00', '', '', '{"show_title":"","link_titles":"","show_intro":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","alternative_readmore":"","article_layout":""}', 6, 0, 9, '', '', 1, 182, '{"robots":"","author":"","rights":"","xreference":""}', 1, 'uk-UA', ''),
(208, 250, 'Кукольный театр', 'kukolnyi-teatr', '', '<p><img src="images/sestrynstvo/fb_img_1458919176908.jpg" width="320" height="240" style="float: left;" />&nbsp;&nbsp;17 марта, в конференц - зале больницы, для детей ОХМАТДЕТА организовали представление кукольного театра. &nbsp; &nbsp; Концерт провели при поддержке детской библиотеки имени Т. Г. Шевченка.&nbsp;</p>\r\n<p>&nbsp; &nbsp;Благодаря сестрам милосердия нашего храма, многие маленькие пациенты, смогли посетить это отрадное и радостное представление.&nbsp;', '<img src="images/sestrynstvo/fb_img_1458924863699.jpg" width="500" height="375" /><img src="images/sestrynstvo/fb_img_1458919176908.jpg" width="500" height="375" /><img src="images/sestrynstvo/fb_img_1458924881722.jpg" width="500" height="375" /><img src="images/sestrynstvo/fb_img_1458924869699.jpg" width="500" height="375" /></p>', 1, 0, 0, 13, '2016-03-20 20:42:01', 43, '', '2016-03-26 19:26:02', 43, 43, '2016-03-26 19:26:02', '2016-03-20 20:42:01', '0000-00-00 00:00:00', '', '', '{"show_title":"","link_titles":"","show_intro":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","alternative_readmore":"","article_layout":""}', 9, 0, 8, '', '', 1, 307, '{"robots":"","author":"","rights":"","xreference":""}', 1, 'ru-RU', ''),
(209, 251, 'Ляльковий театр ', 'lialkovyi-teatr', '', '<p><img src="images/sestrynstvo/fb_img_1458919176908.jpg" width="320" height="240" style="float: left;" />&nbsp; &nbsp;17-го&nbsp;березня, в конференц - залі лікарні, для дітей ОХМАТДИТУ організували виставу лялькового театру.</p>\r\n<p>&nbsp; &nbsp;Концерт провели за підтримки дитячої бібліотеки імені Т. Г. Шевченка.</p>\r\n<p>&nbsp; &nbsp;Завдяки сестрам милосердя нашого храму, багато маленьких пацієнтів, змогли відвідати цю втішну і радісну подію.', '<img src="images/sestrynstvo/fb_img_1458924863699.jpg" width="500" height="375" /><img src="images/sestrynstvo/fb_img_1458919176908.jpg" width="500" height="375" /><img src="images/sestrynstvo/fb_img_1458924881722.jpg" width="500" height="375" /><img src="images/sestrynstvo/fb_img_1458924869699.jpg" width="500" height="375" /></p>', 1, 0, 0, 12, '2016-03-20 20:56:53', 43, '', '2016-03-26 19:28:10', 43, 43, '2016-03-26 19:28:10', '2016-03-20 20:56:53', '0000-00-00 00:00:00', '', '', '{"show_title":"","link_titles":"","show_intro":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","alternative_readmore":"","article_layout":""}', 4, 0, 8, '', '', 1, 257, '{"robots":"","author":"","rights":"","xreference":""}', 1, 'uk-UA', ''),
(210, 252, 'Чудотворна ікона «Всецариця»', 'chudotvorna-ikona-vsetsarytsia', '', '<p>&nbsp;&nbsp; <img src="images/vsecarica/img_5996.jpg" alt="" />&nbsp;&nbsp; Чудотворна ікона «Всецариця» завітала до дитячих відділень столичних клінік.</p>\r\n<p>Сам образ Божої Матері «Всецариця» (по-грецьки - «Пантанасса») знаходиться на Святій Горі Афон у Ватопедському монастирі. На іконі зображено Пречисту Діву у багряному вбранні, що сидить на царському троні, а на її руках знаходиться Богонемовля з сувоєм в лівій руці, та благословляючою правою десницею. Ікона відома численними зціленнями тяжких захворювань (і особливо – онкологічних) тих, хто з вірою молиться перед ним, та благає про допомогу Пречисту Божу Матір. Точний список ікони перебуває у чоловічому монастирі Різдва Пресвятої Богородиці в урочищі Церковщина під Києвом.</p>\r\n<p>&nbsp;&nbsp; Найбільше діточок змогли доторкнутися до чудотворної ікони в Національній дитячій спеціалізованій клініці «ОХМАТДИТ». Настоятель храму Почаївської Божої Матері протоієрей Олексій Артюх та протоієрей Владислав Доломанський, відслужили святковий молебень у трьох основних відділеннях (хірургічному, токсикології та онкогематології), попросивши присутнім зціленні від недуги, та помазали святим єлеєм.', '<img src="images/vsecarica/img_5788.jpg" alt="" /><img src="images/vsecarica/img_5810.jpg" alt="" /><img src="images/vsecarica/img_5811.jpg" alt="" /><img src="images/vsecarica/img_5879.jpg" alt="" /><img src="images/vsecarica/img_5897.jpg" alt="" /><img src="images/vsecarica/img_5935.jpg" alt="" /><img src="images/vsecarica/img_5946.jpg" alt="" /><img src="images/vsecarica/img_5965.jpg" alt="" /><img src="images/vsecarica/img_5996.jpg" alt="" /><img src="images/vsecarica/img_6002.jpg" alt="" /></p>', 1, 0, 0, 12, '2016-04-27 05:19:12', 43, '', '2016-04-27 05:23:09', 43, 43, '2016-04-27 05:23:09', '2016-04-27 05:19:11', '0000-00-00 00:00:00', '', '', '{"show_title":"","link_titles":"","show_intro":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","alternative_readmore":"","article_layout":""}', 5, 0, 7, '', '', 1, 315, '{"robots":"","author":"","rights":"","xreference":""}', 1, '*', ''),
(211, 253, 'Чудотворная икона «Всецарица»', 'chudotvornaia-ykona-vsetsarytsa', '', '<p>&nbsp;&nbsp; <img src="images/vsecarica/img_5996.jpg" alt="" />Чудотворная икона «Всецарица» посетила детские отделения столичных клиник.</p>\r\n<p>&nbsp;&nbsp; Сам образ Божией Матери «Всецарица» (по-Гречески - «Пантанасса») находится на Святой Горе Афон в Ватопедском монастыре. На иконе изображена ПречистАЯ ДевА в багряном одеянии, сидящая на царском троне, а на ее руках находится Богомладенц со свитком в левой руке, и благословляющего правой десницей. Икона известна многочисленными исцелениями тяжелых заболеваний (и особенно - онкологических) тех, кто с верой молиться перед ним, и умоляет о помощи Пречистую Божию Матерь. Точный список иконы находится в мужском монастыре Рождества Пресвятой Богородицы в урочище Церковщина под Киевом.</p>\r\n<p>&nbsp;&nbsp; Большое количество детишек смогли прикоснуться к чудотворной иконе в Национальной детской специализированной клинике «ОХМАТДЕТ». Настоятель храма Почаевской иконы Божьей Матери протоиерей Алексей Артюх и протоиерей Владислав Доломанський, отслужили праздничный молебен в трех основных отделениях (хирургическом, токсикологии и онкогематологии), попросив присутствующим исцеления от недуг, и помазали святым елеем.', '<img src="images/vsecarica/img_5788.jpg" alt="" /><img src="images/vsecarica/img_5810.jpg" alt="" /><img src="images/vsecarica/img_5811.jpg" alt="" /><img src="images/vsecarica/img_5879.jpg" alt="" /><img src="images/vsecarica/img_5897.jpg" alt="" /><img src="images/vsecarica/img_5935.jpg" alt="" /><img src="images/vsecarica/img_5946.jpg" alt="" /><img src="images/vsecarica/img_5965.jpg" alt="" /><img src="images/vsecarica/img_5996.jpg" alt="" /><img src="images/vsecarica/img_6002.jpg" alt="" /></p>', 1, 0, 0, 13, '2016-04-27 11:02:53', 43, '', '2016-05-03 11:07:17', 43, 43, '2016-05-03 11:07:17', '2016-04-27 11:02:53', '0000-00-00 00:00:00', '', '', '{"show_title":"","link_titles":"","show_intro":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","alternative_readmore":"","article_layout":""}', 5, 0, 7, '', '', 1, 226, '{"robots":"","author":"","rights":"","xreference":""}', 1, 'ru-RU', ''),
(212, 254, 'Школа інтернат ', 'shkola-internat', '', '<p><img src="images/Internat/fb_img_1462371452080.jpg" width="640" height="480" />Свято Великодня!</p>\r\n<p>Вже стало доброю традицією відзначати це свято з нашими читачами - учнями школи-інтернату №2. Цього року з солодкими пасками та крашанками разом з Отцем Владиславом - протоієрієм храму Почаївської ікони Божої Матері, працівники відділу мистецтв ЦБ ім. Т. Г. Шевченка для дітей м. Києва завітали до дітей в гості.</p>\r\n<p>Отець Владислав привітав всіх дітей та вихователів зі святом Воскресіння Христова, побажав миру, злагоди і здоров''я та подарував освячені ікони. А куштування обрядового хлібу - куліча, випеченого бібліотекарями спеціально для цих дітей, додало святкового настрою.</p>\r\n<p>ХРИСТОС ВОСКРЕС!!!</p>\r\n<p>істочнік: <a href="https://m.facebook.com/story.php?story_fbid=626944874124365&amp;id=116523891833135">https://m.facebook.com/story.php?story_fbid=626944874124365&amp;id=116523891833135</a>&nbsp;', '<img src="images/Internat/fb_img_1462371454941.jpg" width="640" height="480" /><img src="images/Internat/fb_img_1462371457630.jpg" width="640" height="480" /><img src="images/Internat/fb_img_1462371459928.jpg" width="640" height="480" /><img src="images/Internat/fb_img_1462371465382.jpg" width="640" height="480" /><img src="images/Internat/fb_img_1462371467729.jpg" width="640" height="480" /></p>', 1, 0, 0, 12, '2016-05-05 06:16:41', 43, '', '2016-05-05 06:42:04', 43, 43, '2016-05-05 06:42:04', '2016-05-05 06:16:41', '0000-00-00 00:00:00', '', '', '{"show_title":"","link_titles":"","show_intro":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","alternative_readmore":"","article_layout":""}', 6, 0, 6, '', '', 1, 923, '{"robots":"","author":"","rights":"","xreference":""}', 1, 'uk-UA', ''),
(213, 255, 'Школа интернат', 'shkola-ynternat', '', '<p><span style="font-size: 14pt;"><strong>Подробнее -&nbsp;<a href="index.php?option=com_content&amp;view=article&amp;catid=12&amp;id=212">Школа інтернат</a></strong></span></p>\r\n<p><strong><img src="images/Internat/fb_img_1462371452080.jpg" width="640" height="480" /><span style="font-size: 14pt;"><a href="index.php?option=com_content&amp;view=article&amp;catid=12&amp;id=212">Школа інтернат </a></span></strong></p>', '', 1, 0, 0, 13, '2016-05-05 06:19:48', 43, '', '2016-05-05 06:38:11', 43, 43, '2016-05-05 06:38:11', '2016-05-05 06:19:48', '0000-00-00 00:00:00', '', '', '{"show_title":"","link_titles":"","show_intro":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","alternative_readmore":"","article_layout":""}', 3, 0, 6, '', '', 1, 78, '{"robots":"","author":"","rights":"","xreference":""}', 1, 'ru-RU', ''),
(214, 256, 'Пасхальний концерт ', 'paskhalnyi-kontsert', '', '<p>&nbsp;<img style="float: left;" src="images/pasxa2016/1.jpg" height="180" width="320" />&nbsp; Церква привітала з Пасхою Христовою - усіх дітей ОХМАТДИТУ.</p>\r\n<p>&nbsp;&nbsp; У концертному залі Національної дитячої спеціалізованої клініки «ОХМАТДИТ» для малечі відбувся святковий концентр та вистава Київського муніципального академічного театру ляльок.</p>\r\n<p>&nbsp;&nbsp; Настоятель храму Почаївської ікони Божої Матері протоієрей Олексій Артюх та протоієрей Владислав Доломанський відслужили святкові молебени у відділеннях лікарні та вручили усім дітлахам подарунки.</p>\r\n<p>&nbsp;&nbsp; Висловлюємо щиру подяку усім, хто став причетним до створення справжнього свята для малечі та їх батьків.', '<img src="images/pasxa2016/1.jpg" alt="" /><img src="images/pasxa2016/2.jpg" alt="" /><img src="images/pasxa2016/3.jpg" alt="" /><img src="images/pasxa2016/4.jpg" alt="" /><img src="images/pasxa2016/5.jpg" alt="" /><img src="images/pasxa2016/6.jpg" alt="" /><img src="images/pasxa2016/7.jpg" alt="" /><img src="images/pasxa2016/img_0014.jpg" alt="" /><img src="images/pasxa2016/img_0044.jpg" alt="" /><img src="images/pasxa2016/img_0051.jpg" alt="" /><img src="images/pasxa2016/img_0071.jpg" alt="" /><img src="images/pasxa2016/img_0074.jpg" alt="" /><img src="images/pasxa2016/img_0092 1.jpg" alt="" /><img src="images/pasxa2016/img_0103.jpg" alt="" /></p>', 1, 0, 0, 12, '2016-05-08 15:55:00', 43, '', '2016-05-08 16:03:08', 43, 0, '0000-00-00 00:00:00', '2016-05-08 15:55:00', '0000-00-00 00:00:00', '', '', '{"show_title":"","link_titles":"","show_intro":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","alternative_readmore":"","article_layout"